// Validaci칩n y normalizaci칩n de datos de entrada
const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const body = item.json.body;

    // Validar campos requeridos
    if (!body.name || !body.gender || !body.interests) {
      throw new Error('Campos requeridos: name, gender, interests');
    }

    // Validar foto
    if (!body.photo && !body.photo_url) {
      throw new Error('Se requiere una foto (archivo o URL)');
    }

    // Normalizar datos
    const normalizedData = {
      id: $uuid(),
      name: body.name.trim(),
      gender: body.gender.toLowerCase(),
      interests: body.interests.trim(),
      language: body.language || 'es',
      style: body.style || 'fantasy',
      photo: body.photo,
      photo_url: body.photo_url,
      timestamp: new Date().toISOString(),
      status: 'processing'
    };

    // Validar g칠nero
    if (!['male', 'female', 'other'].includes(normalizedData.gender)) {
      throw new Error('G칠nero debe ser: male, female, other');
    }

    // Validar estilo
    if (!['fantasy', 'adventure', 'educational', 'scientific'].includes(normalizedData.style)) {
      throw new Error('Estilo debe ser: fantasy, adventure, educational, scientific');
    }

    results.push({ json: normalizedData });

  } catch (error) {
    results.push({ 
      json: { 
        error: true, 
        message: error.message,
        timestamp: new Date().toISOString()
      } 
    });
  }
}

return results;

// Verificar cache de Redis
const Redis = require('redis');
const crypto = require('crypto');

const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const data = item.json;

    // Generar clave de cache basada en intereses y estilo
    const cacheKey = crypto
      .createHash('md5')
      .update(`${data.interests}-${data.style}-${data.language}`)
      .digest('hex');

    // Configurar cliente Redis
    const redisClient = Redis.createClient({
      host: process.env.REDIS_HOST || 'localhost',
      port: process.env.REDIS_PORT || 6379,
      password: process.env.REDIS_PASSWORD || undefined
    });

    // Intentar obtener del cache
    let cachedStory = null;
    try {
      await redisClient.connect();
      cachedStory = await redisClient.get(`story:${cacheKey}`);
      await redisClient.disconnect();
    } catch (redisError) {
      console.log('Redis no disponible, continuando sin cache');
    }

    results.push({
      json: {
        ...data,
        cacheKey,
        cachedStory: cachedStory ? JSON.parse(cachedStory) : null,
        useCache: !!cachedStory
      }
    });

  } catch (error) {
    results.push({
      json: {
        ...item.json,
        cacheError: error.message,
        useCache: false
      }
    });
  }
}

return results;

