// Procesar respuesta de Gemini y extraer historia
const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const response = item.json;

    // Extraer contenido de la respuesta de Gemini
    let storyContent;
    if (response.candidates && response.candidates[0] && response.candidates[0].content) {
      const textContent = response.candidates[0].content.parts[0].text;

      // Intentar parsear como JSON
      try {
        storyContent = JSON.parse(textContent);
      } catch (parseError) {
        // Si no es JSON v치lido, crear estructura b치sica
        storyContent = {
          title: `La aventura de ${$('validate-input').item.json.name}`,
          chapters: [
            {
              number: 1,
              title: "El comienzo",
              content: textContent.substring(0, 500),
              image_prompt: `${$('validate-input').item.json.name} comenzando una aventura, estilo cartoon colorido`
            }
          ],
          moral: "Cada aventura nos ense침a algo nuevo",
          word_count: textContent.split(' ').length
        };
      }
    } else {
      throw new Error('Respuesta inv치lida de Gemini');
    }

    // Validar estructura de la historia
    if (!storyContent.title || !storyContent.chapters || !Array.isArray(storyContent.chapters)) {
      throw new Error('Estructura de historia inv치lida');
    }

    // Asegurar que cada cap칤tulo tenga image_prompt
    storyContent.chapters = storyContent.chapters.map((chapter, index) => ({
      ...chapter,
      number: index + 1,
      image_prompt: chapter.image_prompt || `${$('validate-input').item.json.name} en una escena del cap칤tulo ${index + 1}, estilo ilustraci칩n infantil colorida`
    }));

    results.push({
      json: {
        ...item.json,
        story: storyContent,
        processing_step: 'story_generated'
      }
    });

  } catch (error) {
    results.push({
      json: {
        ...item.json,
        error: true,
        message: `Error procesando historia: ${error.message}`,
        processing_step: 'story_error'
      }
    });
  }
}

return results;

// Preparar datos para generaci칩n de imagen
const items = $input.all();
const results = [];

for (const item of items) {
  const storyData = item.json;
  const chapters = storyData.story.chapters;
  const currentBatch = $('split-chapters').context.currentRunIndex;

  if (chapters && chapters[currentBatch]) {
    const chapter = chapters[currentBatch];

    // Crear prompt mejorado para Nanobanana
    const enhancedPrompt = `
      Ilustraci칩n infantil colorida y amigable de: ${chapter.image_prompt}.
      Personaje principal: ${storyData.name}, ${storyData.gender === 'female' ? 'ni침a' : storyData.gender === 'male' ? 'ni침o' : 'ni침@'}.
      Estilo: cartoon, colores vibrantes, apropiado para ni침os.
      Escena: ${chapter.content.substring(0, 200)}...
      Evitar: contenido violento o aterrador.
    `.trim();

    results.push({
      json: {
        ...storyData,
        currentChapter: chapter,
        chapterIndex: currentBatch,
        imagePrompt: enhancedPrompt,
        processing_step: 'preparing_image'
      }
    });
  }
}

return results;

// Procesar imagen generada y subirla a Supabase
const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const data = item.json;

    // Extraer URL de imagen de la respuesta de Nanobanana
    let imageUrl;
    if (data.image_url) {
      imageUrl = data.image_url;
    } else if (data.images && data.images[0]) {
      imageUrl = data.images[0].url || data.images[0];
    } else {
      throw new Error('No se pudo obtener URL de imagen');
    }

    // Preparar datos para guardar en base de datos
    const imageData = {
      story_request_id: data.id,
      image_url: imageUrl,
      image_type: 'illustration',
      prompt_used: data.imagePrompt,
      sequence_order: data.chapterIndex + 1,
      generation_params: {
        width: 1024,
        height: 1024,
        style: 'cartoon',
        ai_model: 'nanobanana'
      },
      ai_model_used: 'nanobanana'
    };

    results.push({
      json: {
        ...data,
        generatedImage: imageData,
        processing_step: 'image_generated'
      }
    });

  } catch (error) {
    results.push({
      json: {
        ...item.json,
        error: true,
        message: `Error procesando imagen: ${error.message}`,
        processing_step: 'image_error'
      }
    });
  }
}

return results;

// Recopilar todas las im치genes generadas y preparar para PDF
const items = $input.all();

// Obtener datos del primer item (todos deber칤an tener la misma historia)
const storyData = items[0].json;

// Recopilar todas las im치genes generadas
const generatedImages = items.map(item => item.json.generatedImage);

// Combinar historia con im치genes
const completeStory = {
  ...storyData,
  story: {
    ...storyData.story,
    chapters: storyData.story.chapters.map((chapter, index) => ({
      ...chapter,
      image_url: generatedImages[index]?.image_url || null
    }))
  },
  generatedImages,
  processing_step: 'preparing_pdf'
};

return [{ json: completeStory }];

// Generar HTML para el PDF con dise침o profesional
