// Generar PDF usando Puppeteer
const puppeteer = require('puppeteer');
const fs = require('fs');
const path = require('path');

const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const data = item.json;
    const htmlContent = data.htmlContent;

    // Configurar Puppeteer
    const browser = await puppeteer.launch({
      headless: true,
      args: ['--no-sandbox', '--disable-setuid-sandbox']
    });

    const page = await browser.newPage();

    // Configurar contenido HTML
    await page.setContent(htmlContent, {
      waitUntil: 'networkidle0',
      timeout: 30000
    });

    // Generar PDF
    const pdfBuffer = await page.pdf({
      format: 'A4',
      printBackground: true,
      margin: {
        top: '0mm',
        right: '0mm',
        bottom: '0mm',
        left: '0mm'
      },
      preferCSSPageSize: true
    });

    await browser.close();

    // Guardar PDF temporalmente
    const tempDir = process.env.PDF_TEMP_PATH || './temp/pdfs';
    if (!fs.existsSync(tempDir)) {
      fs.mkdirSync(tempDir, { recursive: true });
    }

    const fileName = `cuento_${data.id}_${Date.now()}.pdf`;
    const filePath = path.join(tempDir, fileName);

    fs.writeFileSync(filePath, pdfBuffer);

    results.push({
      json: {
        ...data,
        pdfPath: filePath,
        pdfFileName: fileName,
        pdfSize: pdfBuffer.length,
        processing_step: 'pdf_generated'
      }
    });

  } catch (error) {
    results.push({
      json: {
        ...item.json,
        error: true,
        message: `Error generando PDF: ${error.message}`,
        processing_step: 'pdf_error'
      }
    });
  }
}

return results;

// Usar historia del cache
